import React from "react";
import { useForm } from "react-hook-form";

export default function TaskHookForm() {
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      isim: "",
    },
    mode: "onChange",
  });

  const onSubmit = (data) => {
    console.log(data);
  };
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <label>
        Başlık:
        <input
          rows="15"
          id="başlık"
          name="başlık"
          {...register("başlık", {
            required: "Task başlığı yazmalısınız",
            minLength: {
              value: 3,
              message: "Başlık en az 3 karakter içermelidir",
            },
          })}
        ></input>
      </label>
      {errors.mesaj && <p>{errors.mesaj.message}</p>}

      <label>
        Açıklama:
        <textarea
          rows="3"
          id="açıklama"
          name="açıklama"
          {...register("açıklama", {
            required: "Açıklama boş bırakılamaz",
            minLength: {
              value: 10,
              message: "Açıklama en az 10 karakter içermelidir",
            },
          })}
        ></textarea>
      </label>
      {errors.açıklama && <p>{errors.açıklama.message}</p>}

      <button type="submit" disabled={!isValid}>
        Gönder
      </button>

      <label>
        İsim:
        <input
          type="text"
          name="isim"
          {...register("isim", { required: "İsim boş bırakılamaz" })}
        />
      </label>
      <button type="submit">Gönder</button>
      {errors.isim && <p>{errors.isim.message}</p>}
    </form>
  );
}
